package com.strings;

public class StringEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      String s1 = "Naveen";
      System.out.println(s1.startsWith("N"));
      System.out.println(s1.startsWith("Na"));
      System.out.println(s1.startsWith("Nav"));
      System.out.println(s1.endsWith("n"));
      System.out.println(s1.endsWith("een"));
      System.out.println(s1.endsWith("j"));
	}

}
